mapper.select:
SELECT COUNT(*) 
	FROM pay
	WHERE p_mid = #{id}

SELECT COUNT(*)
FROM basket
WHERE ba_mid = #{id}

SELECT TO_CHAR(ddate, 'yyyy-MM-DD') ddate, COUNT(*) cnt
    FROM ( SELECT mys_ddate ddate
        FROM myclasssub
        WHERE mys_mid = #{id}
        AND mys_done = 'Y')
WHERE ddate >= #{start}
    AND ddate  <= #{end}
GROUP BY TO_CHAR(ddate, 'yyyy-MM-DD')
ORDER BY ddate

SELECT mys_runtime
FROM myclasssub
WHERE mys_mid = #{id}
AND mys_code = #{hCode}

mapper.update:
UPDATE myclasssub
SET mys_watchtime = #{wTime}
WHERE mys_mid = #{id}
AND mys_code = #{hCode}
AND mys_done = 'N'

